import { Component } from '@angular/core';

@Component({
  selector: 'app-task2',
  templateUrl: './task2.component.html',
  styleUrls: ['./task2.component.css']
})
export class Task2Component {
  componentXList: any[] = [];

  addComponentX() {
    const newComponentX = { name: `X${this.componentXList.length + 1}` };
    this.componentXList.push(newComponentX);
  }
}
